import { createStore } from 'vuex'

export default createStore({
  state() {
    return {
        pokemons: [],
        colors: {
            normal: '#A8A77A',
            fire: '#EE8130',
            water: '#6390F0',
            electric: '#F7D02C',
            grass: '#7AC74C',
            ice: '#96D9D6',
            fighting: '#C22E28',
            poison: '#A33EA1',
            ground: '#E2BF65',
            flying: '#A98FF3',
            psychic: '#F95587',
            bug: '#A6B91A',
            rock: '#B6A136',
            ghost: '#735797',
            dragon: '#6F35FC',
            dark: '#705746',
            steel: '#B7B7CE',
            fairy: '#D685AD',
        },
        currentPokemon: []       
    }
  },
  getters: {
  },
  mutations: {
    SET_POKEMONS(state, pokemons) {
        state.pokemons.push(...pokemons) 
    },
    SET_CURRENT_POKEMON(state, pokemon) {
      state.currentPokemon.shift()
      state.currentPokemon.push(pokemon)
    }
  },
  actions: {
    async addPokemons({commit}, interval) {
      const Pokedex = require("pokeapi-js-wrapper")
      const P = new Pokedex.Pokedex({ cacheImages: true })

      const pokemonList = await P.getPokemonsList(interval);
      const globalPokemonList = await Promise.all(
        pokemonList.results.map((pokemon_min) => P.getPokemonByName(pokemon_min.name))
      )
      // pokemonList.results.forEach(async pokemon_min => {
      //   const pokemon = await P.getPokemonByName(pokemon_min.name)
      //   globalPokemonList.push(pokemon)
      // })
      commit('SET_POKEMONS', globalPokemonList)
      // .then(function(pokemon_global) {
      //   commit('SET_POKEMONS', pokemon_global)
      // })
      // .then(function(response) {
      //     response.results.forEach(pokemon_min => {
      //         P.getPokemonByName(pokemon_min.name).then(function(pokemon_global) {
      //             commit('SET_POKEMONS', pokemon_global)
      //         })
      //     })
      // })      
    },
    addCurrentPokemonById({commit}, id) {
      const Pokedex = require("pokeapi-js-wrapper")
      const P = new Pokedex.Pokedex({ cacheImages: true })

      P.getPokemonByName(id).then(function(pokemon) {
          commit('SET_CURRENT_POKEMON', pokemon)
      })  
    }
  },
  modules: {
  }
})
