// import { generate } from '@vue/compiler-core'
import { createStore } from 'vuex'

export default createStore({
  state() {
    return {
      language: "fr",
      pokedex: new (require("pokeapi-js-wrapper")).Pokedex({ cacheImages: true }),
      interval: { offset: 0, limit: 10 },
      pokemons: [],
      matchs: [],
      colors: {
          normal: '#A8A77A',
          fire: '#EE8130',
          water: '#6390F0',
          electric: '#F7D02C',
          grass: '#7AC74C',
          ice: '#96D9D6',
          fighting: '#C22E28',
          poison: '#A33EA1',
          ground: '#E2BF65',
          flying: '#A98FF3',
          psychic: '#F95587',
          bug: '#A6B91A',
          rock: '#B6A136',
          ghost: '#735797',
          dragon: '#6F35FC',
          dark: '#705746',
          steel: '#B7B7CE',
          fairy: '#D685AD'
      },
      cacheNameList: []
      // currentPokemon: []  
    }
  },
  getters: {
  },
  mutations: {
    RESET_INTERVALE(state) {
      state.interval.offset = 0
      state.interval.limit = 10
    },
    UPDATE_INTERVALE(state) {
      state.interval.offset += state.interval.limit
    },
    EMPTY_POKEMONS(state) {
      state.pokemons = []
    },
    PUSH_POKEMON(state, pokemon) {
      state.pokemons.push(pokemon) 
    },
    PUSH_CURRENT_POKEMON(state, pokemon) {
      state.currentPokemon = [pokemon]
    },
    PUSH_MATCHS(state, matchs) {
      state.matchs = matchs
    },
    SETUP_CACHE_NAMES(state, nameList) {
      state.cacheNameList = nameList
    }
  },
  actions: {
    async initCacheNames({ state, commit, dispatch }) {
      const lang = state.language    // français
      const pokeList = await state.pokedex.getPokemonSpeciesList()
      const nameList = await Promise.all(
        pokeList.results.map(async (poke) => {  
          let pokeSpecies = await state.pokedex.getPokemonSpeciesByName(poke.name)
          let tradName = await dispatch('findNameByLang', { names: pokeSpecies.names, lang: lang })
          return { name: poke.name, traduction: tradName }
        })
      )
      commit('SETUP_CACHE_NAMES', nameList)
    },
    findNameByLang({}, { names, lang }) {
      let i = 0
      while(!(names[i].language.name === lang)) {
        i++
      }
      return names[i].name
    },
    async createPokemon({ commit, state, dispatch }, { nameList, lang }) {
      for (const name of nameList) {
        let pokemon = {}
        let poke = await state.pokedex.getPokemonByName(name)
        let pokeSpecies = await state.pokedex.getPokemonSpeciesByName(name)

        // id
        let id = poke.id
        pokemon.id = id
        // name
        pokemon.name = await dispatch('findNameByLang', { names: pokeSpecies.names, lang: lang })
        // types
        let types = []
        for (const type of poke.types) {
          let t = await state.pokedex.getTypeByName(type.type.name)
          let tname = await dispatch('findNameByLang', { names: t.names, lang: lang })
          types.push(tname)
        }
        pokemon.types = types
        // color
        let color = state.colors[poke.types[0].type.name]
        pokemon.color = color
        // image
        let image = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/${id}.svg`
        pokemon.image = image

        // add pokemon to pokemons
        // commit('PUSH_POKEMON', pokemon)
      }
    },
    async generatePokemons({ state, commit, dispatch }) {
      const lang = state.language    // français
      const pokeList = await state.pokedex.getPokemonSpeciesList(state.interval)
      const nameList = await Promise.all(
        pokeList.results.map((pokemon) => {  return pokemon.name})
      )
      dispatch('createPokemon', { nameList: nameList, lang: lang })
      commit('UPDATE_INTERVALE')
    },
    async generatePokemonsBySearch({ state, commit, dispatch }, search) {
      commit('RESET_INTERVALE')
      commit('EMPTY_POKEMONS')
      commit('PUSH_MATCHS', [])
      if (search === '') {
        dispatch('generatePokemons')
      } else {
        const lang = state.language    // français
        // const pokeList = await state.pokedex.getPokemonSpeciesList()
        // const nameList = await Promise.all(
        //   pokeList.results.map(async (poke) => {  
        //     let pokeSpecies = await state.pokedex.getPokemonSpeciesByName(poke.name)
        //     let tradName = await dispatch('findNameByLang', { names: pokeSpecies.names, lang: lang })
        //     return { name: poke.name, traduction: tradName }
        //   })
        // )
        const filterNameList = state.cacheNameList.filter((name) => {
          return name.traduction.toLowerCase().includes(search.toLowerCase())
        })
        const results = filterNameList.map((name) => {  return name.name})
        for (const name of results) {
          let pokemon = dispatch('createPokemon', { name: name, lang: lang })
          commit('PUSH_POKEMON', pokemon)
        }

      }
    },
    async generateMatchsPokemons({commit, state }, search) {
      if (search === '') {
        commit('PUSH_MATCHS', [])
      } else {
        // const lang = state.language     // français
        // const pokeList = await state.pokedex.getPokemonSpeciesList()
        // const nameList = await Promise.all(
        //   pokeList.results.map(async (poke) => {  
        //     let pokeSpecies = await state.pokedex.getPokemonSpeciesByName(poke.name)
        //     let tradName = await dispatch('findNameByLang', { names: pokeSpecies.names, lang: lang })
        //     return { name: poke.name, traduction: tradName }
        //   })
        // )
        let matched = 0
        const matchs = state.cacheNameList.filter((name) => {
          return name.traduction.toLowerCase().includes(search.toLowerCase()) && matched++ < 10
        })
        commit('PUSH_MATCHS', matchs)
      }
    }
  },
  modules: {
  }
})
