import { generate } from '@vue/compiler-core'
import { createStore } from 'vuex'

export default createStore({
  state() {
    return {
        pokedex: new (require("pokeapi-js-wrapper")).Pokedex({ cacheImages: true }),
        interval: { offset: 0, limit: 10 },
        pokemons: [],
        matchedNames: [],
        colors: {
            normal: '#A8A77A',
            fire: '#EE8130',
            water: '#6390F0',
            electric: '#F7D02C',
            grass: '#7AC74C',
            ice: '#96D9D6',
            fighting: '#C22E28',
            poison: '#A33EA1',
            ground: '#E2BF65',
            flying: '#A98FF3',
            psychic: '#F95587',
            bug: '#A6B91A',
            rock: '#B6A136',
            ghost: '#735797',
            dragon: '#6F35FC',
            dark: '#705746',
            steel: '#B7B7CE',
            fairy: '#D685AD'
        },
        currentPokemon: []  
    }
  },
  getters: {
  },
  mutations: {
    INIT_INTERVALE(state) {
      state.interval.offset = 0
      state.interval.limit = 10
    },
    UPDATE_INTERVALE(state) {
      state.interval.offset += state.interval.limit
    },
    EMPTY_POKEMONS(state) {
      state.pokemons = []
    },
    PUSH_POKEMONS(state, pokemons) {
      state.pokemons.push(...pokemons) 
    },
    PUSH_POKEMON(state, pokemon) {
      state.pokemons.push(pokemon) 
    },
    PUSH_CURRENT_POKEMON(state, pokemon) {
      state.currentPokemon = [pokemon]
    },
    UPDATE_MATCHED_NAMES(state, names) {
      state.matchedNames = names
    },
    EMPTY_MATCHED_NAMES(state) {
      state.matchedNames = []
    }
  },
  actions: {
    findNameByLang({ names, lang }) {
      let i = 0
      while(names[i].language.name !== lang) {
        i++
      }
      return names[i].name
    },
    async createPokemon({ commit, state, dispatch }, { nameList, lang }) {
      const pokemon = {}
      for (const name of nameList) {
        let poke = await state.pokedex.getPokemonByName(name)
        let pokeSpecies = await state.pokedex.getPokemonSpeciesByName(name)

        // id
        let id = poke.id
        pokemon.id = id
        // name
        let n = pokeSpecies.names
        pokemon.name = dispatch('findNameByLang', { n, lang })
        // types
        let types = []
        for (const type of poke.types) {
          let t = await state.pokedex.getTypeByName(type.type.name)
          let tnames = t.names
          let tname = dispatch('findNameByLang', { tnames, lang })
          types.push(tname)
        }
        pokemon.types = types
        // color
        let color = state.colors[poke.types[0].type.name]
        pokemon.color = color
        // image
        let image = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/${id}.svg`
        pokemon.image = image

        // add pokemon to pokemons
        commit('PUSH_POKEMON', pokemon)
      }
    },
    async generatePokemons({ state, dispatch }) {
      const pokeList = await state.pokedex.getPokemonsList()
      const nameList = await Promise.all(
        pokeList.results.map((pokemon) => {  return pokemon.name})
      )
      const lang = 4    // fr,
      dispatch('createPokemon', { nameList, lang })
    },
    async addPokemons({ commit, state }, { reset, search }) {
      let result = []
      if (reset) {
        commit('INIT_INTERVALE')
        commit('EMPTY_POKEMONS')
      }
      if (search === null || search === '') {
        const pokemons = await state.pokedex.getPokemonsList(state.interval)
        result = await Promise.all(
          pokemons.results.map((pokemon) => {  return state.pokedex.getPokemonByName(pokemon.name)})
        )
        commit('UPDATE_INTERVALE')
      } else {
        const pokemons = await state.pokedex.getPokemonsList()
        const names = await Promise.all(
          pokemons.results.map((pokemon) => {  return pokemon.name})
        )
        const filteredNames = names.filter((name) => {
          return name.includes(search)
        })
        for (const name of filteredNames) {
          const pokemon = await state.pokedex.getPokemonByName(name)
          result.push(pokemon)
        }
      }
      commit('PUSH_POKEMONS', result) 
    },
    async addCurrentPokemon({commit, state }, search) {
      const pokemon = await state.pokedex.getPokemonByName(search)
      commit('PUSH_CURRENT_POKEMON', pokemon)
      commit('UPDATE_MATCHED_NAMES', [])
    },
    async matchPokemons({commit, state }, search) {
      let matched = 0
      const pokemons = await state.pokedex.getPokemonsList()
      const names = await Promise.all(
        pokemons.results.map((pokemon) => {  return pokemon.name})
      )
      const filteredNames = names.filter((name) => {
        return name.includes(search) && matched++ < 10
      })
      commit('UPDATE_MATCHED_NAMES', filteredNames)
    },
    emptyMatchedNames({commit}) {
      commit('EMPTY_MATCHED_NAMES')
    }
  },
  modules: {
  }
})
