import { createStore } from 'vuex'

export default createStore({
  state() {
    return {
        pokemons: [],
        colors: {
            normal: '#A8A77A',
            fire: '#EE8130',
            water: '#6390F0',
            electric: '#F7D02C',
            grass: '#7AC74C',
            ice: '#96D9D6',
            fighting: '#C22E28',
            poison: '#A33EA1',
            ground: '#E2BF65',
            flying: '#A98FF3',
            psychic: '#F95587',
            bug: '#A6B91A',
            rock: '#B6A136',
            ghost: '#735797',
            dragon: '#6F35FC',
            dark: '#705746',
            steel: '#B7B7CE',
            fairy: '#D685AD',
        },
        currentPokemon: []       
    }
  },
  getters: {
    async addCurrentPokemonById({commit}, id) {
      const Pokedex = require("pokeapi-js-wrapper")
      const P = new Pokedex.Pokedex({ cacheImages: true })

      const pokemon = await P.getPokemonByName(id)
      commit('SET_CURRENT_POKEMON', pokemon)
    }
  },
  mutations: {
    PUSH_POKEMONS(state, pokemons) {
      state.pokemons.push(...pokemons) 
    },
    SET_CURRENT_POKEMON(state, pokemon) {
      state.currentPokemon = [pokemon]
    }
  },
  actions: {
    async addPokemons({commit}, interval) {
      const Pokedex = require("pokeapi-js-wrapper")
      const P = new Pokedex.Pokedex({ cacheImages: true })

      const pokemonList = await P.getPokemonsList(interval)
      const globalPokemonList = await Promise.all(
        pokemonList.results.map((pokemon) => {  return P.getPokemonByName(pokemon.name)})
      )
      commit('PUSH_POKEMONS', globalPokemonList)  
      }
  },
  modules: {
  }
})
