import { createStore } from 'vuex'

export default createStore({
  state() {
    return {
        pokedex: new (require("pokeapi-js-wrapper")).Pokedex({ cacheImages: true }),
        interval: { offset: 0, limit: 10 },
        pokemons: [],
        colors: {
            normal: '#A8A77A',
            fire: '#EE8130',
            water: '#6390F0',
            electric: '#F7D02C',
            grass: '#7AC74C',
            ice: '#96D9D6',
            fighting: '#C22E28',
            poison: '#A33EA1',
            ground: '#E2BF65',
            flying: '#A98FF3',
            psychic: '#F95587',
            bug: '#A6B91A',
            rock: '#B6A136',
            ghost: '#735797',
            dragon: '#6F35FC',
            dark: '#705746',
            steel: '#B7B7CE',
            fairy: '#D685AD'
        },
        currentPokemon: [],
        pokemonsMatched: []     
    }
  },
  getters: {
  },
  mutations: {
    PUSH_POKEMONS(state, pokemons) {
      state.pokemons.push(...pokemons) 
    },
    SET_CURRENT_POKEMON(state, pokemon) {
      state.currentPokemon = [pokemon]
    },
    SET_MATCHS(state, listName) {
      state.pokemonsMatched.push(...listName)
    },
    UPDATE_INTERVALE(state) {
      state.interval.offset += state.interval.limit
    }
  },
  actions: {
    async addPokemons({ commit, state }) {
      const pokemonList = await state.pokedex.getPokemonsList(state.interval)
      const globalPokemonList = await Promise.all(
        pokemonList.results.map((pokemon) => {  return state.pokedex.getPokemonByName(pokemon.name)})
      )
      commit('PUSH_POKEMONS', globalPokemonList) 
      commit('UPDATE_INTERVALE') 
    },
    async addCurrentPokemon({commit, state }, searched) {
      const pokemon = await state.pokedex.getPokemonByName(searched)
      commit('SET_CURRENT_POKEMON', pokemon)
      commit('SET_MATCHS', [])
    },
    async matchPokemons({commit, state }, search) {
      const pokemons = await state.pokedex.getPokemonsList()
      const globalPokemonList = await Promise.all(
        pokemons.results.map((pokemon) => {  return state.pokedex.getPokemonByName(pokemon.name)})
      )
      // const listPokemonsName = pokemons.results.map((pokemon) => {  return pokemon.name})
      let matched = 0
      globalPokemonList.filter((pokemon) => {
        return pokemon.name.startsWith(search) && matched++ < 10
      })
      commit('SET_MATCHS', globalPokemonList)
    }
  },
  modules: {
  }
})
