import { createStore } from 'vuex'

export default createStore({
  state() {
    return {
        Pokedex: (new require("pokeapi-js-wrapper").Pokedex({ cacheImages: true })),
        pokemons: [],
        colors: {
            normal: '#A8A77A',
            fire: '#EE8130',
            water: '#6390F0',
            electric: '#F7D02C',
            grass: '#7AC74C',
            ice: '#96D9D6',
            fighting: '#C22E28',
            poison: '#A33EA1',
            ground: '#E2BF65',
            flying: '#A98FF3',
            psychic: '#F95587',
            bug: '#A6B91A',
            rock: '#B6A136',
            ghost: '#735797',
            dragon: '#6F35FC',
            dark: '#705746',
            steel: '#B7B7CE',
            fairy: '#D685AD',
        }

    }
  },
  getters: {
  },
  mutations: {
    SET_POKEMONS(state, pokemon) {
        state.pokemons.push(pokemon)
    }
  },
  actions: {
    addPokemons({commit, state}, offset, limit) {
        // const Pokedex = require("pokeapi-js-wrapper")
        // const P = new Pokedex.Pokedex({ cacheImages: true })
        const P = state.Pokedex
        P.getPokemonsList(offset, limit).then(function(response) {
            response.results.forEach(pokemon => {
                P.getPokemonByName(pokemon.name).then(function(pokemonDetail) {
                    commit('SET_POKEMONS', pokemonDetail)
                })
            });
        })      
    }
  },
  modules: {
  }
})
