import { createStore } from 'vuex'

export default createStore({
  state() {
    return {
      language: "fr",
      pokedex: new (require("pokeapi-js-wrapper")).Pokedex({ cache: true }),
      interval: { limit: 20, offset: 0 },
      pokemons: [],
      matchs: [],
      colors: {
          normal: '#A8A77A',
          fire: '#EE8130',
          water: '#6390F0',
          electric: '#F7D02C',
          grass: '#7AC74C',
          ice: '#96D9D6',
          fighting: '#C22E28',
          poison: '#A33EA1',
          ground: '#E2BF65',
          flying: '#A98FF3',
          psychic: '#F95587',
          bug: '#A6B91A',
          rock: '#B6A136',
          ghost: '#735797',
          dragon: '#6F35FC',
          dark: '#705746',
          steel: '#B7B7CE',
          fairy: '#D685AD'
      },
      cacheNameList: [],
      next: null
      // currentPokemon: []  
    }
  },
  getters: {
  },
  mutations: {
    RESET_INTERVALE(state) {
      state.interval.offset = 0
      state.interval.limit = 20
    },
    UPDATE_INTERVALE(state) {
      state.interval.offset += 20
    },
    EMPTY_POKEMONS(state) {
      state.pokemons = []
    },
    PUSH_POKEMONS(state, pokemons) {
      state.pokemons.push(...pokemons) 
    },
    PUSH_CURRENT_POKEMON(state, pokemon) {
      state.currentPokemon = [pokemon]
    },
    PUSH_MATCHS(state, matchs) {
      state.matchs = matchs
    },
    SETUP_CACHE_NAMES(state, nameList) {
      state.cacheNameList = nameList
    },
  },
  actions: {
    async initCacheNames({ state, commit, dispatch }) {
      const pokeList = await state.pokedex.getPokemonsList()
      const nameList = await Promise.all(
        pokeList.results.map(async (p) => {
          const poke = await state.pokedex.resource(p.url)
          const pokeSpecies =  await state.pokedex.resource(poke.species.url)
          let tradName = await dispatch('findByLang', pokeSpecies.names)
          return { id: poke.id, name: poke.name, traduction: tradName }
        })
      )
      commit('SETUP_CACHE_NAMES', nameList)
    },
    findByLang({ state }, list) {
      const found = list.find((elem) => {
        return elem.language.name === state.language
      })
      return found.name || found.flavor_text
    },
    async createPokemon({ state, dispatch }, name) {
      const pokemon = {}
      const poke = await state.pokedex.getPokemonByName(name)
      const pokeSpecies = await state.pokedex.resource(poke.species.url)

      // id
      pokemon.id = poke.id
      // name
      pokemon.name = await dispatch('findByLang', pokeSpecies.names)
      // types
      const types = await Promise.all(
        poke.types.map(async (type) => {  
          let t = await state.pokedex.getTypeByName(type.type.name)
          return { name: await dispatch('findByLang', t.names), color: state.colors[t.name]}
        })
      )
      pokemon.types = types
      // color
      pokemon.color = state.colors[poke.types[0].type.name]
      // image
      pokemon.image = `https://raw.githubusercontent.com/PokeAPI/sprites/master/sprites/pokemon/other/dream-world/${poke.id}.svg`
      // evolution chain
      const evolutions = []
      let evolution_chain = (await state.pokedex.resource(pokeSpecies.evolution_chain.url)).chain
      while (evolution_chain.evolves_to.length > 0) {
        evolutions.push({ name: evolution_chain.species.name, evoLvl: evolution_chain.evolves_to[0].evolution_details[0].min_level })
        evolution_chain = evolution_chain.evolves_to[0]
      }
      evolutions.push({ name: evolution_chain.species.name, evoLvl: false })
      pokemon.evolutions = evolutions
      // legendary
      pokemon.legendary = pokeSpecies.is_legendary
      // description
      pokemon.description = await dispatch('findByLang', pokeSpecies.flavor_text_entries)
      // specifications
      pokemon.specifications = { height: poke.height/10, weight: poke.weight/10, category: "pas trouvé", attacks: "pas trouvé"}

      return pokemon
    },
    async generatePokemons({ state, commit, dispatch }) {
      const pokeList = await state.pokedex.getPokemonsList(state.interval)
      const pokemons = await Promise.all(
        pokeList.results.map(async (pokemon) => {  return await dispatch('createPokemon', pokemon.name)})
      )

      commit('PUSH_POKEMONS', pokemons)
      commit('UPDATE_INTERVALE')
    },
    async generatePokemonsBySearch({ state, commit, dispatch }, search) {
      commit('RESET_INTERVALE')
      commit('EMPTY_POKEMONS')
      commit('PUSH_MATCHS', [])

      if (!(search === '')) {
        const filterNameList = state.cacheNameList.filter((poke) => {
          return poke.traduction.toLowerCase().includes(search.toLowerCase()) || parseInt(search) === poke.id
        })
        const pokemons = await Promise.all(
          filterNameList.map(async (poke) => {  return await dispatch('createPokemon', poke.name)})
        )

        commit('PUSH_POKEMONS', pokemons)
      } else {
        dispatch('generatePokemons')
      }
    },
    async generateMatchsPokemons({commit, state }, search) {
      if (search === '') {
        commit('PUSH_MATCHS', [])
      } else {
        let matched = 0
        const matchs = state.cacheNameList.filter((name) => {
          return name.traduction.toLowerCase().includes(search.toLowerCase()) && matched++ < 10
        })

        commit('PUSH_MATCHS', matchs)
      }
    }
  },
  modules: {
  }
})
