import { createStore } from 'vuex'

export default createStore({
  state() {
    return {
        pokemons: []
    }
  },
  getters: {
  },
  mutations: {
    SET_POKEMONS(state, payload) {
        state.pokemons = payload
    }
  },
  actions: {
    wrapperPokemons({commit}, payload) {
        const Pokedex = require("pokeapi-js-wrapper")
        const P = new Pokedex.Pokedex()
        const interval = {
            offset: 0,
            limit: 10,
        }
        const pokemons = []
        P.getPokemonsList(interval).then(function(response) {
            response.results.forEach(pokemon => {
                P.getPokemonByName(pokemon.name).then(function(pokemonDetail) {
                    // console.log(pokemonDetail)
                    pokemons.push(pokemonDetail)
                    console.log(pokemons)
                })
            });
        })
        this.$store.commit('SET_POKEMONS', pokemons) 
        commit('SET_POKEMONS', pokemons)
    }
  },
  modules: {
  }
})
